import Head from 'next/head';
import styles from '../styles/Home.module.css';

const Logo = () => (
  <div className={styles.logoWrapper}>
    <img src='./graphic-progression.svg' width='64' height='48' />
    <p className={styles.companyName}>Укргазбудпроект</p>
  </div>
);

const isNotEmptyArr = arr => arr.length > 0;

const activity = {
  engineeringNetworks: [
    {
      title: 'ОВіК',
      price: '100 грн',
      services: [
        'Проектування систем опалення',
        'Проектуання систем вентиляції',
        'Проектування систем кондиціонування',
      ],
    },
    {
      title: 'ЕТР',
      price: '100 грн',
      services: [
        'Електропостачання (прокладання кабеля, повітряне, СІП- самонесучий ізольований провід). Силове обладнання. Системи зовнішнього та внутрішнього освітлення. Блискавкозахист, заземлення. Паспорти блискавкозахисту паспорт заземлення, паспорти на повітряні кабельні лінії та паспорти електроприміщення'
      ],
    },
    {
      price: '100 грн',
      title: 'Cлабострумні Kабельні Cистеми',
      services: [
        'Проектування телекомунікаційних мереж (телебачення, телефонія, інет)',
        'Проектування охоронних системи',
        'Проектвування систем пожежогасіння та димовидалення',
        'Проектування систем відеонагляду',
      ],
    },
    {
      services: [],
      price: '100 грн',
      title: 'Oцінка впливу на довкілля',
    },
    {
      services: [],
      price: '100 грн',
      title: 'Eнергозбереження та Pозробка Eнергопаспорта',
    },
  ],
  ecological: [
    {
      services: [],
      price: '100 грн',
      title: 'Розробка документації на викиди в атмосферне повітря',
    },
    {
      services: [],
      price: '100 грн',
      title: 'Оцінка впливу на довкілля',
    },
    {
      services: [],
      price: '100 грн',
      title: 'Рзробка індивідуальних балансових норм водоспоживання та водовідведення',
    },
    {
      services: [],
      price: '100 грн',
      title: 'Паспортизація відходів',
    },
  ],
};

const ActivitySection = ({ activityName, activityTitle }) => (
  <div className={styles.activityWrapper}>
    <div className={styles.activityHeader}>
      {activityTitle}
    </div>
    <div className={styles.columnTitles}>
      <div className={styles.columnItem}>Розділ</div>
      <div className={styles.servicesColumnTitle}>Послуги</div>
    </div>
    {
      activity[activityName].map(({ title, services }, index) => (
        <div
          key={index}
          // todo: fix this hack
          style={{
            width: '100%',
            height: 'auto',
            minHeight: 50,
            display: 'flex',
            borderColor: 'grey',
            borderBottom: index === activity[activityName].length - 1 ? 'none' : '1px solid',
          }}
        >
          <div className={styles.columnItem}>{index + 1}. {title}</div>
          {
            isNotEmptyArr(services) &&
            <div className={styles.servicesWrapper}>
              {
                services.map((item, serviceIndex) => (
                  <div
                    key={serviceIndex}
                    // todo: fix this hack
                    style={{
                      padding: 10,
                      borderColor: 'grey',
                      borderBottom: serviceIndex === services.length - 1 ? 'none' : '1px solid',
                    }}
                  >
                    {item}
                  </div>
                ))
              }
            </div>
          }
        </div>
      ))
    }
  </div>
);

export default function Home() {
  return (
    <div className={styles.container}>
      <Head>
        <title>Укргазбудпроект</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>
      <header className={styles.header}>
        <div className={styles.headerContent}>
          <Logo />
          <div className={styles.contacts}>
            <div style={{ fontWeight: 'bold', marginRight: 10 }}>
              Телефон:
            </div>
            <div>
              <a
                href='tel:0683377070'
                className={styles.contactPhone}
              >
                (068) 337-70-70
              </a>
              <a
                href='tel:0503786869'
                className={styles.contactPhone}
              >
                (050) 378-68-69
              </a>
              <a
                href='tel:0676790808'
                className={styles.contactPhone}
              >
                (067) 679-08-08
              </a>
            </div>
          </div>
        </div>
      </header>
      <section className={styles.frontSection}>
        <h1 className={styles.frontTitle}>ТзОВ "Укргазбудпроект" - Bаш надійний партнер </h1>
        <img src='./home.jpg' className={styles.frontImage} />
      </section>
      <main className={styles.mainWrapper}>
        <h2 className={styles.companyActivityTitle}>
          Діяльність Kомпанії
        </h2>
        <ActivitySection activityName='engineeringNetworks' activityTitle='Проектування інженерних мереж' />
        <ActivitySection activityName='ecological' activityTitle='Послуги екологічного характеру' />
      </main>
      <footer className={styles.footer}>
        <div className={styles.headerContent}>
          <Logo />
          <div className={styles.contacts}>
            <div style={{ fontWeight: 'bold', marginRight: 10 }}>
              Телефон:
            </div>
            <div>
              <a
                href='tel:0683377070'
                className={styles.contactPhone}
              >
                (068) 337-70-70
              </a>
              <a
                href='tel:0503786869'
                className={styles.contactPhone}
              >
                (050) 378-68-69
              </a>
              <a
                href='tel:0676790808'
                className={styles.contactPhone}
              >
                (067) 679-08-08
              </a>
            </div>
          </div>
        </div>
      </footer>
    </div>
  )
}
